  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "APEX_230100"."APEX_APPLICATION_PAGE_PROC" ("WORKSPACE", "WORKSPACE_DISPLAY_NAME", "APPLICATION_ID", "APPLICATION_NAME", "PAGE_ID", "PAGE_NAME", "PROCESS_NAME", "EXECUTION_SEQUENCE", "PROCESS_POINT", "PROCESS_POINT_CODE", "REGION_ID", "REGION_NAME", "EXECUTION_CHAIN_ID", "EXECUTION_CHAIN_NAME", "LOCATION", "REMOTE_DATABASE_NAME", "REMOTE_DATABASE_ID", "WEB_SOURCE_MODULE_NAME", "WEB_SOURCE_MODULE_ID", "WEB_SOURCE_OPERATION_ID", "PROCESS_TYPE", "PROCESS_TYPE_CODE", "PROCESS_TYPE_PLUGIN_NAME", "RUNTIME_WHERE_CLAUSE", "PROCESS_SOURCE", "PROCESS_SOURCE_LANGUAGE", "ATTRIBUTE_01", "ATTRIBUTE_02", "ATTRIBUTE_03", "ATTRIBUTE_04", "ATTRIBUTE_05", "ATTRIBUTE_06", "ATTRIBUTE_07", "ATTRIBUTE_08", "ATTRIBUTE_09", "ATTRIBUTE_10", "ATTRIBUTE_11", "ATTRIBUTE_12", "ATTRIBUTE_13", "ATTRIBUTE_14", "ATTRIBUTE_15", "PROCESS_ERROR_MESSAGE", "ERROR_DISPLAY_LOCATION", "WHEN_BUTTON_PRESSED", "WHEN_BUTTON_PRESSED_ID", "ONLY_FOR_CHANGED_ROWS", "CONDITION_TYPE", "CONDITION_TYPE_CODE", "CONDITION_EXPRESSION1", "CONDITION_EXPRESSION2", "EXEC_COND_FOR_EACH_ROW", "RUN_PROCESS", "RETURN_KEY_INTO_ITEM_1", "RETURN_KEY_INTO_ITEM_2", "SUCCESS_MESSAGE", "BUILD_OPTION", "AUTHORIZATION_SCHEME", "AUTHORIZATION_SCHEME_ID", "LAST_UPDATED_BY", "LAST_UPDATED_ON", "COMPONENT_COMMENT", "PROCESS_ID", "INTERNAL_UID", "COMPONENT_SIGNATURE") AS 
  select
     f.workspace,
     f.workspace_display_name,
     f.application_id,
     f.application_name,
     p.id                           page_id,
     p.name                         page_name,
     --
     pr.process_name                process_name,
     pr.process_sequence            execution_sequence,
     --
     decode(pr.process_point,
       'RETURN_VALUE','DISPLAY_VALUE',
       'ON_NEW_INSTANCE','On New Instance After Authentication',
       'BEFORE_HEADER','On Load - Before Header',
       'AFTER_HEADER','On Load - After Header',
       'BEFORE_BOX_BODY','On Load - Before Regions',
       'AFTER_BOX_BODY','On Load - After Regions',
