    i.attribute_21,
    i.attribute_22,
    i.attribute_23,
    i.attribute_24,
    i.attribute_25,
    --
    i.ITEM_SEQUENCE                 display_sequence,
    i.fc_max_height                 max_height,
    case i.fc_show_label
      when 'Y' then 'Yes'
      when 'N' then 'No'
    end                             show_label,
