       e.condition_expr1,
       e.condition_expr2,
        case e.exec_cond_for_each_row
         when 'Y' then 'Yes'
         when 'N' then 'No'
       end                          as exec_cond_for_each_row,
       --
       coalesce (
           ( select ss.name
               from wwv_flow_security_schemes ss
