  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "APEX_230100"."APEX_DEBUG_MESSAGES" ("ID", "PAGE_VIEW_ID", "MESSAGE_TIMESTAMP", "ELAPSED_TIME", "EXECUTION_TIME", "MESSAGE", "APPLICATION_ID", "PAGE_ID", "SESSION_ID", "APEX_USER", "MESSAGE_LEVEL", "WORKSPACE_ID", "CALL_STACK") AS 
  select id,
       page_view_id,
       message_timestamp,
       elap as elapsed_time,
       extract(second from (lead(message_timestamp) over (partition by page_view_id order by id) - message_timestamp)) +
       extract(minute from (lead(message_timestamp) over (partition by page_view_id order by id) - message_timestamp)) * 60 as execution_time,
       message,
       flow_id as application_id,
       page_id,
       session_id,
       apex_user,
       message_level,
       security_group_id as workspace_id,
       call_stack
  from ( select m.*
           from wwv_flow_debug_messages m,
                wwv_flow_current_workspaces0 w
          where w.workspace_id = m.security_group_id
          union all
         select m.*
           from wwv_flow_debug_messages2 m,
                wwv_flow_current_workspaces0 w
          where m.security_group_id = w.workspace_id );

   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."ID" IS 'Unique ID of the message';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."PAGE_VIEW_ID" IS 'Page View Identifier, which is a unique sequence generated for each page view recorded with debugging';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."MESSAGE_TIMESTAMP" IS 'Timestamp in GMT that message was saved';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."ELAPSED_TIME" IS 'Elapsed time in seconds from the beginning of the page submission or page view';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."EXECUTION_TIME" IS 'Time elapsed between the current and the next debug message';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."MESSAGE" IS 'Debug message';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."APPLICATION_ID" IS 'Application Identifier, unique over all workspaces';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."PAGE_ID" IS 'Page Identifier within the specified application';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."SESSION_ID" IS 'APEX Session Identifier';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."APEX_USER" IS 'Username of the user authenticated to the APEX application';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."MESSAGE_LEVEL" IS 'Level of debug message, ranging from 1 to 9';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."WORKSPACE_ID" IS 'Oracle APEX Workspace Identifier, unique over all workspaces';
   COMMENT ON COLUMN "APEX_230100"."APEX_DEBUG_MESSAGES"."CALL_STACK" IS 'PL/SQL call stack where the message was written';
   COMMENT ON TABLE "APEX_230100"."APEX_DEBUG_MESSAGES"  IS 'Debug messages that were recorded for page views'
;
  GRANT READ ON "APEX_230100"."APEX_DEBUG_MESSAGES" TO PUBLIC;
