--------------------------------------------------------

  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "APEX_230100"."APEX_DG_BUILTIN_GENERIC_PRODS" ("PRODUCT_NAME", "LIST_PRICE", "VC2", "LANG") AS 
  select
       gp.vc1
      || c.color
      || coalesce(
         fs.attr, q1.attr,
         q2.attr,
         q3.attr
      ) product_name,
      gp.n1 * nvl(
         coalesce(
            q1.qty, q2.qty,
            q3.qty
         ), 1
      ) list_price,
      gp.vc2,
      gp.lang
   from
      wwv_dg_helper_data gp
      left outer join (
         select
            ' ('
            || color
            || ')' color,
            lang
         from
            apex_dg_builtin_colors
      ) c on gp.lang = c.lang
             and gp.vc3 = 'Y'
      full outer join (
         select
            ' - ' || trim(column_value) attr
         from
            table ( wwv_flow_string.split(
               'XL,L,M,S', ','
            ) )
      ) fs on gp.vc4 = 'FIT_SIZE'
      full outer join (
         select
            ' ('
            || substr(
               trim(column_value), 4
            )
            || ')' attr,
            to_number(
               substr(
                  trim(column_value), 0,
                  2
               )
            ) qty
         from
            table ( wwv_flow_string.split(
               '01 Single,02 2 Pack,04 4 Pack,08 8 Pack,12 12 Pack', ','
            ) )
      ) q1 on gp.vc4 = 'QTY_1'
      full outer join (
         select
            ' ('
            || substr(
               trim(column_value), 4
            )
            || ')' attr,
            to_number(
               substr(
                  trim(column_value), 0,
                  2
               )
            ) qty
         from
            table ( wwv_flow_string.split(
               '01 2 Pack,03 6 Pack,06 12 Pack', ','
            ) )
      ) q2 on gp.vc4 = 'QTY_2'
      full outer join (
         select
            ' ('
            || substr(
               trim(column_value), 4
            )
            || ')' attr,
            to_number(
               substr(
                  trim(column_value), 0,
                  2
               )
            ) qty
         from
            table ( wwv_flow_string.split(
               '01 10 Pack,03 30 Pack,06 60 Pack', ','
            ) )
      ) q3 on gp.vc4 = 'QTY_3'
   where gp.domain = 'GENERIC_PRODS'
;

   COMMENT ON COLUMN "APEX_230100"."APEX_DG_BUILTIN_GENERIC_PRODS"."PRODUCT_NAME" IS 'Generic product name';
   COMMENT ON COLUMN "APEX_230100"."APEX_DG_BUILTIN_GENERIC_PRODS"."LIST_PRICE" IS 'List price';
