  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "APEX_230100"."APEX_REST_RESOURCE_MODULES" ("WORKSPACE_ID", "WORKSPACE", "WORKSPACE_DISPLAY_NAME", "MODULE_ID", "MODULE_NAME", "URI_PREFIX", "PARSING_SCHEMA", "ORIGINS_ALLOWED", "ITEMS_PER_PAGE", "PRIVILEGE", "TEMPLATES", "STATUS", "COMMENTS", "ROW_VERSION_NUMBER", "CREATED_BY", "CREATED_ON", "LAST_UPDATED_BY", "LAST_UPDATED_ON") AS 
  select
    w.workspace_id,
    w.workspace,
    w.workspace_display_name,
    m.id                                              module_id,
    m.name                                            module_name,
    m.uri_prefix                                      uri_prefix,
    m.parsing_schema                                  parsing_schema,
    m.origins_allowed                                 origins_allowed,
    m.items_per_page                                  items_per_page,
    (select label from wwv_flow_rt$privileges
      where id = m.privilege_id
        and security_group_id = m.security_group_id)  privilege,
    (select count(*) from wwv_flow_rt$templates
      where module_id = m.id
        and security_group_id = m.security_group_id)  templates,
    m.status                                          status,
    m.comments                                        comments,
    m.row_version_number                              row_version_number,
    m.created_by,
    m.created_on,
    m.UPDATED_BY                                      last_updated_by,
    m.UPDATED_ON                                      last_updated_on
from wwv_flow_rt$modules m,
     wwv_flow_current_workspaces w
where m.security_group_id = w.workspace_id;

   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."WORKSPACE_ID" IS 'Oracle APEX Workspace Identifier, unique over all workspaces';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."WORKSPACE" IS 'A work area mapped to one or more database schemas';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."WORKSPACE_DISPLAY_NAME" IS 'Display name for the workspace';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."MODULE_ID" IS 'Identfies the ID of the module';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."MODULE_NAME" IS 'Display name for the module';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."URI_PREFIX" IS 'Identifies the URI template prefix';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."PARSING_SCHEMA" IS 'Display name for the parsing schema';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."ORIGINS_ALLOWED" IS 'A comma-separated list of origins which are permitted to access the resource template';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."ITEMS_PER_PAGE" IS 'Identifies the size of the pagination window';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."PRIVILEGE" IS 'Identifies the privilege required to access this module';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."TEMPLATES" IS 'Identifies the number of templates associated with the module';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."STATUS" IS 'Identifies whether the RESTful Services Modules are published and available for use, or non published are not available';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."COMMENTS" IS 'Displays the comments on the handler';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."ROW_VERSION_NUMBER" IS 'Displays the row version number';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."CREATED_BY" IS 'User that created this module';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."CREATED_ON" IS 'Date this module was created';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."LAST_UPDATED_BY" IS 'APEX developer who made last update';
   COMMENT ON COLUMN "APEX_230100"."APEX_REST_RESOURCE_MODULES"."LAST_UPDATED_ON" IS 'Date of last update';
   COMMENT ON TABLE "APEX_230100"."APEX_REST_RESOURCE_MODULES"  IS 'RESTful services modules'
;
  GRANT READ ON "APEX_230100"."APEX_REST_RESOURCE_MODULES" TO PUBLIC;
